import React from "react";

const Header = ({ name }) => (
  <h1>{name}</h1>
)
/*
const Content = ({ parts }) => {
  return (
    <ul >
      {parts.map(part => <li key={part.id} >{part.name} {part.exercises}</li>)}
      <li>total of {parts.reduce((sum, part) => sum+part.exercises, 0)} exercises</li>
    </ul>
  )
}
*/
const Course = ({name, parts}) => {
  return (
  <Header name={name} />
  )
}

const Courses = ({ courses }) => {
  console.log(courses)
  return (
    <div>
      <ul style={{'list-style-type': 'none'}}>
        {courses.map(course =>
          <li><Course key={course.id} name={course.name} parts={course.parts}/></li>)}
      </ul>
    </div>
  )
}
const App = () => {
  const courses = [{
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      },
      {
        name: 'Redux',
        exercises: 11,
        id: 4
      }
    ]
  }, 
  {
    name: 'Node.js',
    id: 2,
    parts: [
      {
        name: 'Routing',
        exercises: 3,
        id: 1
      },
      {
        name: 'Middlewares',
        exercises: 7,
        id: 2
      }
    ]
  }
]

  return <Courses courses={courses} />
}

export default App;
