import React, { useState } from 'react'

const Person = ({ person }) => {
  return (
    <li>{person.name} {person.number}</li>
  )
}

const PersonForm = (props) => {
  return (<div>
  <form onSubmit={props.addNumber}>
  <div>
    name: <input value={props.newName} onChange={props.handleNameChange} />
  </div>
  <div>
    number <input value={props.newNumber} onChange={props.handleNumberChange} />
  </div>
  <div>
    <button type="submit">add</button>
  </div>
</form>
</div>)
}

const Filter = ({newFilter, handleSearch}) => {
  return (
    <div>
    filter shown with <input value={newFilter} onChange={handleSearch}/>
    </div>
  )
}
const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '345 00 00' },
    { name: 'Ada Lovelace', number: '39-44-5323523'},
    { name: 'Dan Abramov', number: '12-43-234345'},
    { name: 'Mary Poppendieck', number: '39-23-6423122'}
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')

  const addNumber = (event) => {
    event.preventDefault()

    if (persons.find(({name})=> name===newName)) {
      window.alert(newName + ' is already added to phonebook');

    } else {
      const NumberObject = {
        name: newName,
        number: newNumber
      }

      setPersons(persons.concat(NumberObject))
      setNewName('')
      setNewNumber('')
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearch = (event) => {
    setNewFilter(event.target.value)
  }

  const filteredPersons = persons.filter((person) => person.name.toLowerCase().startsWith(newFilter.toLowerCase()))
  .map(person =>
    <Person key={person.name} person={person}/>)

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter newFilter={newFilter} handleSearch={handleSearch}/>
      <h2>add a new</h2>
      < PersonForm addNumber={addNumber} newName={newName} handleNameChange={handleNameChange} 
      newNumber={newNumber} handleNumberChange={handleNumberChange}/>
      <h2>Numbers</h2>
      <ul>
        {filteredPersons}
      </ul>
    </div>
  )
}

export default App
