import React, { useState, useEffect } from 'react'
import axios from 'axios'
import PersonForm from './components/PersonForm'
import Persons from './components/Persons'
import Filter from './components/Filter'

const App = () => {
  const [persons, setPersons] = useState([]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')

  useEffect(() => {
    axios
    .get('http://localhost:3001/persons')
    .then(response => {
      setPersons(response.data) 
    })
  }, [])

  const addNumber = (event) => {
    event.preventDefault()

    if (persons.find(({name})=> name===newName)) {
      window.alert(newName + ' is already added to phonebook');

    } else {
      const NumberObject = {
        name: newName,
        number: newNumber,
        id:persons.length + 1
      }

      axios
        .post('http://localhost:3001/persons', NumberObject)
        .then(response => {
          setPersons(persons.concat(response.data))
          setNewName('')
          setNewNumber('')
        })
    }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearch = (event) => {
    setNewFilter(event.target.value)
  }

  const deletePerson = id => {
    console.log(id)
    const changedPersons = persons.filter(person => person.id === id)
    console.log(changedPersons)
    /*axios
      .put('http://localhost:3001/persons', ) */
  }
  return (
    <div>
      <h2>Phonebook</h2>
      <Filter newFilter={newFilter} handleSearch={handleSearch}/>
      <h2>add a new</h2>
      <PersonForm addNumber={addNumber} newName={newName} handleNameChange={handleNameChange} 
      newNumber={newNumber} handleNumberChange={handleNumberChange}/>
      <h2>Numbers</h2>
      <ul>
        <Persons persons={persons} newFilter={newFilter} deletePerson={deletePerson}/>
      </ul>
    </div>
  )
}

export default App
